import React, { useEffect, useState } from 'react';
import fetch from 'node-fetch'

function HomePage() {

  const [loginInfo, setLoginInfo] = useState({"email": '', "password": ''})

  useEffect(() => {
    fetch('http://localhost:3000/currentuser')
    .then(res => {
      return res.json()
    })
    .then(result => {

      if (result.error) {
        throw result.error
      } 
      setData(result)
    })
    .catch(err => {
      console.log(err)
      setData([err])
    })

  }, []);

  const handleSubmit = (evt) => {
    evt.preventDefault();
    setLoginInfo({"email": '', "password": ''})
    alert(`Submitting Login Info`)
  }

  const handleFormChange = (evt) => {
    let newInfo = Object.assign({}, loginInfo)
    newInfo[evt.target.name] = evt.target.value
    setLoginInfo(newInfo)
  }
  return (
  <div>
    Welcome to Videotastic!
    <form onSubmit={handleSubmit}>
      <label>
        Email: 
        <input onChange={handleFormChange} type="text" name="email" value={loginInfo.email}/>
      </label>
      <label>
        Password: 
        <input onChange={handleFormChange} type="password" name="password" value={loginInfo.password} />
      </label>
      <input type="submit" value="Login" />
    </form>

  </div>
  )
}

export default HomePage
